	public static List<List<Integer>> threeSum(int[] nums) {
		List<List<Integer>> res = new ArrayList<List<Integer>>();
		Set<List<Integer>> tempres = new HashSet<List<Integer>>();
		//Arrays.parallelSort(nums);
		
		System.out.println(Arrays.toString(nums));
		int sum = 0;
		HashMap<Integer, List> map = new HashMap<>();

		for (int i = 0; i < nums.length; i++) {
			List<Integer> list;
			if (map.containsKey((Integer) nums[i])) {
				list = map.get(nums[i]);
			} else {
				list = new ArrayList<>();
			}
			list.add(i);
			map.put(nums[i], list);
		}

		for (int i = 0; i < nums.length - 2; i++) {
			for (int j = i + 1; j < nums.length-1; j++) {
				int rem = nums[i] + nums[j];
				//System.out.println(nums[i]+"==="+nums[j]+"=="+(sum-rem));
				if (map.containsKey(sum - rem)) {
					List<Integer> list = map.get(sum - rem);
					if (!list.isEmpty()) {
						for (int k = 0; k < list.size(); k++) {
							if(list.get(k) == i || list.get(k)==j)continue;
							List<Integer> el = new ArrayList<>();
							el.add(nums[i]);
							el.add(nums[j]);
							el.add(nums[list.get(k)]);
							Collections.sort(el);
							if(tempres.add(el)) {
								System.out.println("--------"+nums[i]+" "+nums[j]);
								res.add(el);
							}
						}
					}
				}
			}
		}
		return res;
	}
	
	
	
	    public List<List<Integer>> threeSum1(int[] nums) {
	        int target =0;
	    	HashSet<List<Integer>> hashSet = new HashSet<>();
	    	List<List<Integer>> output = new ArrayList<>();
	    	List<Integer> out = new ArrayList<Integer>();
	        HashMap<Integer, Integer> map = new HashMap<>();
	        for (int i =2; i<nums.length; i++){
	            map.put(nums[i], i);
	        }
	        for (int i =0; i<nums.length-2; i++){
	        	for(int j=i+1 ; j<nums.length; j++){
	        		int findVal = target - (nums[i] + nums[j]);
	        		if(map.containsKey(findVal) && map.get(findVal) != i && map.get(findVal) != j){
	        			int mapIndex =  map.get(findVal);
	        			if(mapIndex != i && mapIndex != j){
	        				out = new ArrayList<Integer>();
	        				out.add(nums[i]);
	        				out.add(nums[j]);
	        				out.add(nums[map.get(findVal)]);
	        				Collections.sort(out);
	        				hashSet.add(out);
	        			}
	        		}
	        	}
	        }
	        output.addAll(hashSet);
	        return output;
	    }
	    
	    
	    
